import React from "react";

class SearchWeather extends React.Component{
    state = {
        name : ""
    }

    search = (e) => {
        e.preventDefault();
        if(this.state.name === ""){
            alert("Name is Mandatory!");
            return;
        }
        this.props.addSearchHandler(this.state);
        this.setState({name : ""});
        // console.log("log " + this.state);

    }
    
    render() {
        
        return (
            <div class="ui placeholder segment">
                <div class="ui stackable very relaxed one column grid">
                    <div class="column">
                        <form class="ui form" onSubmit={this.search}>
                            <div class="field">
                                <label>Country Name</label>
                                <div class="ui left icon input">
                                    <input type="text" name = "name" placeholder="Example : India" value={this.state.name} onChange={(e) => this.setState({name : e.target.value})} />
                                    <i aria-hidden="true" class="map pin icon"></i>
                                </div>
                            </div>
                            {/* <div class="field">
                                <label>Password</label>
                                <div class="ui left icon input">
                                    <input type="password"/>
                                    <i aria-hidden="true" class="lock icon"></i>
                                </div>
                            </div> */}
                            <button class="ui primary button"><i aria-hidden="true" class="search icon"></i>Search</button>
                        </form>
                    </div>
                    {/* <div class="middle aligned column">
                        <button class="ui big button">
                            <i aria-hidden="true" class="signup icon"></i>Sign up
                        </button>
                    </div> */}
                </div>
                {/* <div class="ui vertical divider">Or</div> */}
                </div>
            );
    }
}

export default SearchWeather;
